import React from "react";
import "./page-header.scss";
import { tabs } from "../../constants";
import { NavLink } from "react-router-dom";

const renderHeaderTabs = (tabs) => {
    return tabs.map((currentTab) => {
        return (
            <NavLink
                to={currentTab.path}
                key={currentTab.id}
                className="page-header__tab"
                activeClassName="page-header__tab--active"
                exact
            >
                {currentTab.tab}
            </NavLink>
        );
    });
};

const PageHeader = ({ user, logout, units }) => {
    return (
        <header className="page-header">
            <div className="page-header__wrapper wrapper">
                <img
                    className="page-header__logo"
                    src="./img/latoken.svg"
                    alt="logo"
                />
                <ul className="page-header__tabs">{renderHeaderTabs(tabs)}</ul>
                <form
                    onSubmit={(evt) => {
                        evt.preventDefault();
                        logout();
                    }}
                    className="page-header__profile"
                >
                    <span>{user.name}</span>
                    <button className="btn btn--logout">Выйти</button>
                </form>
            </div>
        </header>
    );
};

export default PageHeader;
