import React from "react";

class Projects extends React.Component {
    render() {
        return(<body>
            <div className="svg-container">
                <svg viewBox="0 0 800 400" className="svg">
                    <path id="curve" fill="#50c6d8" d="M 800 300 Q 400 350 0 300 L 0 0 L 800 0 L 800 300 Z">
                    </path>
                </svg>
            </div>

            <header>
                <h1>This is a header title</h1>
                <h3>Here you are, scroll down.</h3>
            </header>

            <main>
                <p>And the main section.</p>
            </main>

            <footer>
                <p>And, the footer.</p>
                <small>ðŸ•· Wish you luck, <a href="http://armantaherian.com">Arman</a>.</small>
            </footer>
            </body>
        );
    }
}
(function() {
  // Variables
  var $curve = document.getElementById("curve");
  var last_known_scroll_position = 0;
  var defaultCurveValue = 350;
  var curveRate = 3;
  var ticking = false;
  var curveValue;

  // Handle the functionality
  function scrollEvent(scrollPos) {
    if (scrollPos >= 0 && scrollPos < defaultCurveValue) {
      curveValue = defaultCurveValue - parseFloat(scrollPos / curveRate);
      $curve.setAttribute(
        "d",
        "M 800 300 Q 400 " + curveValue + " 0 300 L 0 0 L 800 0 L 800 300 Z"
      );
    }
  }

  // Scroll Listener
  // https://developer.mozilla.org/en-US/docs/Web/Events/scroll
  window.addEventListener("scroll", function(e) {
    last_known_scroll_position = window.scrollY;

    if (!ticking) {
      window.requestAnimationFrame(function() {
        scrollEvent(last_known_scroll_position);
        ticking = false;
      });
    }

    ticking = true;
  });
})();

export default Projects;
