import React from "react";
import "./filters.scss";

const Filters = ({
	user,
	tableData,
	units,
	roles,
	setFilterUnits,
	setFilterRoles,
	rolesBasedOnUnits,
}) => {
	const renderOptionsUnit = (user, list) => {
		if (user.rights === "Regular" || user.rights === "Unit Lead") {
			return <option value={user.unit}>{user.unit}</option>;
		}
		return (
			<React.Fragment>
				<option value="All">All</option>
				{list.map((item) => {
					return (
						<option key={item} value={item}>
							{item}
						</option>
					);
				})}
			</React.Fragment>
		);
	};

	const renderOptionsRole = (user, list) => {
		if (user.rights === "Regular") {
			return <option value={user.role}>{user.role}</option>;
		}
		return (
			<React.Fragment>
				<option value="All">All</option>
				{list.map((item) => {
					return (
						<option key={item} value={item}>
							{item}
						</option>
					);
				})}
			</React.Fragment>
		);
	};

	const unitList = units
		.map((unit) => {
			if (unit.fields.Unit) {
				return unit.fields.Unit;
			}
			return "";
		})
		.filter((value, index, self) => {
			return self.indexOf(value) === index;
		});

	return (
		<ul className="filters">
			<li>
				<span>Unit</span>
				<select
					id="units"
					name="units"
					disabled={
						user.rights === "Regular" || user.rights === "Unit Lead"
							? "disabled"
							: ""
					}
					onChange={(evt) => {
						setFilterUnits(evt.target.value);
						setFilterRoles("All");
					}}
				>
					{renderOptionsUnit(user, unitList)}
				</select>
			</li>
			<li>
				<span>Role</span>
				<select
					id="roleunit"
					name="roleunit"
					disabled={user.rights === "Regular" ? "disabled" : ""}
					onChange={(evt) => {
						setFilterRoles(evt.target.value);
					}}
				>
					{renderOptionsRole(user, rolesBasedOnUnits)}
				</select>
			</li>
		</ul>
	);
};

export default Filters;
