import React from "react";
import "./profile.scss";
import UnitWorkspace from "../unitWorkspace/unitWorkspace";
import CheckList from "../checkListForm/checkListForm";

const getUnitWorkspaceUrl = (units, unit) => {
    let url = null;
    units.forEach((item) => {
        if (item.fields.Unit === unit) {
            url = item.fields["Unit Workspace"];
            return url;
        }
    });
    return url;
};

const renderWorkspaceLink = (units, unit) => {
    let workspaceUrl = getUnitWorkspaceUrl(units, unit);
    if (workspaceUrl) {
        return <UnitWorkspace workspaceUrl={workspaceUrl}></UnitWorkspace>;
    }
    return null;
};

const Profile = ({ units, user, tasksOnRole, isTasksPopup, showPopup }) => {
    return (
        <main className="page-main">
            <div className="page-main__wrapper wrapper">
                <section className="page-main__profile profile">
                    <h1 className="profile__title">Profile info</h1>
                    <div className="profile__info">
                        <ul className="profile__user-info">
                            <li className="profile__user-item profile__user-item--user">
                                <img
                                    className="profile-icon"
                                    src="./img/user-icon.jpg"
                                    alt="user-icon"
                                />
                                User: &nbsp;
                                <span className="item-bold">
                                    {user.name}
                                </span>
                            </li>
                            <li className="profile__user-item profile__user-item--unit">
                                <img
                                    className="profile-icon"
                                    src="./img/role-icon.jpeg"
                                    alt="role-icon"
                                />
                                Role: &nbsp;
                                <span className="item-bold">
                                    {user.role}
                                </span>
                            </li>
                            <li className="profile__user-item profile__user-item--unit">
                                <img
                                    className="profile-icon"
                                    src="./img/team-icon.png"
                                    alt="team-icon"
                                />
                                Unit: &nbsp;
                                <span className="item-bold">
                                    {user.unit}
                                </span>
                            </li>
                            <li>{renderWorkspaceLink(units, user.unit)}</li>
                        </ul>
                        <button
                            onClick={() => {
                                showPopup(!isTasksPopup);
                            }}
                            className="btn"
                            type="button"
                        >
                            {" "}
                            Заполнить чеклист
                        </button>
                    </div>
                </section>
                {isTasksPopup ? (
                    <CheckList
                        tasks={tasksOnRole}
                        showPopup={showPopup}
                        isTasksPopup={isTasksPopup}
                    />
                ) : null}
            </div>
        </main>
    );
};

export default Profile;
